package org.schlibbuz.aoc2021.day16;

import java.util.List;
import static org.testng.Assert.assertEquals;
import org.testng.annotations.DataProvider;
import org.testng.annotations.Test;

/**
 *
 * @author Stefan Frei <stefan.a.frei@gmail.com>
 */
public class DecoderTest {

  @DataProvider(name = "hexToBinFix")
  Object[][] hexToBinFix() {
    return new Object[][] {
      {
        List.of(
            "1"
        ),
        "00000001"
      },
      {
        List.of(
            "FA"
        ),
        "11111010"
      },
      {
        List.of(
            ""
        ),
        ""
      },
    };
  }
  @Test(dataProvider = "hexToBinFix")
  public void hexToBinTest(List<String> data, String exp) {
    var inst = new Decoder(data);
    assertEquals(inst.hexToBin(inst.src), exp);
  }
}
